mapper.select:
SELECT a.no, title, content, writer, id, views, article_group articleGroup, use, a.regdate FROM articles a, users u WHERE a.writer = u.no AND use = 'Y'
AND id = #{id}
ORDER BY article_group DESC, no

mapper.insert:
INSERT INTO articles(no, title, content, writer, article_group)
VALUES (articles_seq.nextval, #{title}, #{content}, #{writer}, 

	#{articleGroup}
	articles_seq.currval

)

mapper.update:
UPDATE articles
SET use = 'N'
WHERE no = #{no} OR article_group = #{no}
